import { useState } from "react";
import { Mic, MicOff, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";

interface VoiceReminderButtonProps {
  onReminderCreated?: (reminder: any) => void;
  variant?: "default" | "sm" | "lg";
}

export function VoiceReminderButton({ onReminderCreated, variant = "default" }: VoiceReminderButtonProps) {
  const [isListening, setIsListening] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [progress, setProgress] = useState(0);
  const { toast } = useToast();

  const startListening = async () => {
    try {
      setIsListening(true);
      setTranscript("");
      setProgress(0);

      // Simulate voice recognition process
      const interval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            return 100;
          }
          return prev + 10;
        });
      }, 300);

      // Simulate voice input
      setTimeout(() => {
        setTranscript("Send Ramesh a 1200 rupees reminder for Friday");
        clearInterval(interval);
        setIsListening(false);
        processVoiceInput("Send Ramesh a 1200 rupees reminder for Friday");
      }, 3000);

    } catch (error) {
      toast({
        title: "Voice Recognition Error",
        description: "Could not access microphone. Please check permissions.",
        variant: "destructive",
      });
      setIsListening(false);
    }
  };

  const processVoiceInput = async (text: string) => {
    setIsProcessing(true);

    try {
      // Simulate AI processing to extract customer name, amount, and date
      await new Promise(resolve => setTimeout(resolve, 1500));

      const extractedData = {
        id: Date.now().toString(),
        customerName: "Ramesh Kumar",
        amount: "Γé╣1,200",
        dueDate: "Friday",
        phone: "+91 98765 43210",
        status: "pending",
        originalText: text,
        createdAt: new Date().toISOString()
      };

      onReminderCreated?.(extractedData);

      toast({
        title: "Voice Reminder Created",
        description: `Payment reminder for ${extractedData.customerName} has been created.`,
      });

    } catch (error) {
      toast({
        title: "Processing Error",
        description: "Failed to process voice input. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsProcessing(false);
      setProgress(0);
    }
  };

  const stopListening = () => {
    setIsListening(false);
    setProgress(0);
  };

  if (variant === "sm") {
    return (
      <Button
        size="sm"
        variant={isListening ? "destructive" : "gradient"}
        onClick={isListening ? stopListening : startListening}
        disabled={isProcessing}
        className="gap-2"
      >
        {isProcessing ? (
          <Loader2 className="w-4 h-4 animate-spin" />
        ) : isListening ? (
          <MicOff className="w-4 h-4" />
        ) : (
          <Mic className="w-4 h-4" />
        )}
        {isProcessing ? "Processing..." : isListening ? "Stop" : "Voice Reminder"}
      </Button>
    );
  }

  return (
    <div className="space-y-4">
      <Button
        variant={isListening ? "destructive" : "gradient"}
        size={variant === "lg" ? "lg" : "default"}
        onClick={isListening ? stopListening : startListening}
        disabled={isProcessing}
        className="gap-2 w-full"
      >
        {isProcessing ? (
          <Loader2 className="w-5 h-5 animate-spin" />
        ) : isListening ? (
          <MicOff className="w-5 h-5" />
        ) : (
          <Mic className="w-5 h-5" />
        )}
        {isProcessing ? "Processing Voice..." : isListening ? "Stop Recording" : "≡ƒÄÖ∩╕Å Voice Payment Reminder"}
      </Button>

      {(isListening || isProcessing) && (
        <Card className="border-primary bg-primary-muted">
          <CardContent className="p-4">
            <div className="flex items-center gap-3 mb-3">
              <div className={`w-10 h-10 bg-primary rounded-full flex items-center justify-center ${isListening ? 'animate-pulse' : ''}`}>
                <Mic className="w-5 h-5 text-primary-foreground" />
              </div>
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-1">
                  <h4 className="font-semibold text-primary">
                    {isListening ? "Listening..." : "Processing..."}
                  </h4>
                  <Badge variant="outline" className="text-xs">
                    {isListening ? "Recording" : "AI Processing"}
                  </Badge>
                </div>
                <p className="text-sm text-primary/80">
                  {isListening 
                    ? 'Say: "Send [Customer] a [Amount] reminder for [Date]"'
                    : 'Extracting customer details...'
                  }
                </p>
              </div>
            </div>

            {transcript && (
              <div className="mb-3 p-2 bg-white/50 rounded text-sm">
                <strong>Heard:</strong> "{transcript}"
              </div>
            )}

            <Progress value={progress} className="h-2" />
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import { createContext, useContext, useEffect, useState } from "react";

type Theme = "dark" | "light" | "system";

type ThemeProviderProps = {
  children: React.ReactNode;
  defaultTheme?: Theme;
  storageKey?: string;
};

type ThemeProviderState = {
  theme: Theme;
  setTheme: (theme: Theme) => void;
};

const initialState: ThemeProviderState = {
  theme: "system",
  setTheme: () => null,
};

const ThemeProviderContext = createContext<ThemeProviderState>(initialState);

export function ThemeProvider({
  children,
  defaultTheme = "system",
  storageKey = "vyapaar-ui-theme",
  ...props
}: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(
    () => (localStorage.getItem(storageKey) as Theme) || defaultTheme
  );

  useEffect(() => {
    const root = window.document.documentElement;

    root.classList.remove("light", "dark");

    if (theme === "system") {
      const systemTheme = window.matchMedia("(prefers-color-scheme: dark)")
        .matches
        ? "dark"
        : "light";

      root.classList.add(systemTheme);
      return;
    }

    root.classList.add(theme);
  }, [theme]);

  const value = {
    theme,
    setTheme: (theme: Theme) => {
      localStorage.setItem(storageKey, theme);
      setTheme(theme);
    },
  };

  return (
    <ThemeProviderContext.Provider {...props} value={value}>
      {children}
    </ThemeProviderContext.Provider>
  );
}

export const useTheme = () => {
  const context = useContext(ThemeProviderContext);

  if (context === undefined)
    throw new Error("useTheme must be used within a ThemeProvider");

  return context;
};

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default: "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary: "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive: "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

export interface BadgeProps extends React.HTMLAttributes<HTMLDivElement>, VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return <div className={cn(badgeVariants({ variant }), className)} {...props} />;
}

export { Badge, badgeVariants };

